mapper.select:
select * from qna

	where subject like #{keyword}


	where writer like #{keyword}

order by num desc

select count(*) from qna

	where subject like #{keyword}


	where writer like #{keyword}

mapper.update:
update qna
set writer=#{writer},cateopen=#{cateopen},
cate=#{cate},subject=#{subject},content=#{content},image=#{image}
where num = #{num}

update qna
set reply='답변완료' 
where num = #{num}
